import React, { Component } from "react";
import api from "../../services/api";
import { MdCreate,MdFiberNew,MdChevronLeft, MdChevronRight } from "react-icons/md";
import { EventList,Hea } from "./styles";

export default class Main extends Component {
  // eslint-disable-next-line react/state-in-constructor
  state = {
    events: []
  };

  async componentDidMount() {
    const response = await api.get("/events");
    this.setState({ events: response.data });
    const data = response.data.map(event => {
      return {
        ...event,
        t: false,
      }

    });
    this.setState({ events: data });
  }

  render() {
    const { events } = this.state;
    console.log(events);

    return (
      <EventList>
        <header>
            <button type="button">
                <MdChevronLeft size={36} color="#000" />
            </button>
            <strong>JANEIRO</strong>
            <button type="button" >
                <MdChevronRight size={36} color="#000" />
                </button>
        </header>
        {events.map(e => (
          <li key={events.id}>
            <img src={e.File.url} alt={e.id} />
            <strong>{e.attraction}</strong>
            <span>{e.name}</span>
            <div>
            <button type="button" onClick={() => this.handleEditEvent(e.id)}>
              <div>
                <MdCreate size={19} color="#FFF" />
              </div>
              <span>Editar Evento</span>
            </button>
            <button className="newButton" type="button" >
              <div>
              <MdFiberNew size={19} color="#FFF" />
              </div>
              <span>Novo Evento</span>
            </button>
            </div>
          </li>
        ))}
      </EventList>
    );
  }
}
