import React, { Component, useEffect } from "react";
import api from "../../services/api";
import {
  MdCreate,
  MdFiberNew,
  MdChevronLeft,
  MdChevronRight
} from "react-icons/md";
import { EventList, Container } from "./styles";
import { format, subMonths, addMonths } from "date-fns";

export default function Main() {
  const [events, setEvents] = useState([]);

  useEffect(()=> {
    async function loadEvents(){
    const response = await api.get("/events");
    this.setState({ events: response.data });
    const data = response.data.map(event => {
      return {
        ...event,
        t: false
      };
    });
    setEvents(data);
  }
  loadEvents();

  });


    return (
      <Container>
        <header>
          <button type="button" onClick={handlePrevMonth}>
            <MdChevronLeft size={46} color="#fff" />
          </button>
          <strong>{formated}</strong>
          <button type="button" onClick={handleNextMonth}>
            <MdChevronRight size={46} color="#fff" />
          </button>
        </header>
        <EventList>
          {events.map(e => (
            <li key={events.id}>
              <img src={e.File.url} alt={e.id} />
              <strong>{e.attraction}</strong>
              <span>{e.name}</span>
              <div>
                <button
                  type="button"
                  onClick={() => this.handleEditEvent(e.id)}
                >
                  <div>
                    <MdCreate size={19} color="#FFF" />
                  </div>
                  <span>Editar Evento</span>
                </button>
                <button className="newButton" type="button">
                  <div>
                    <MdFiberNew size={19} color="#FFF" />
                  </div>
                  <span>Novo Evento</span>
                </button>
              </div>
            </li>
          ))}
        </EventList>
      </Container>
    );

}
